import React from 'react';
import {
    Platform,
    Button,
    TextInput,
    Text,
    View,
    TouchableNativeFeedback,
    TouchableOpacity,
    TouchableHighlight,
    StyleSheet,
  } from 'react-native';
import style from '../global-styles';
import { Character } from './character';


interface CreateProp {
    navigation: any;
}

export  function CharacterCreationComponent({ navigation }: CreateProp) {
    const char: Character = new Character();

    function submitForm(){

    }
    return(
        <View style={style.container}>
        <Text>Character Name: </Text>
        <TextInput
                style={style.input}
                 onChangeText={(value) =>
                    submitForm()
                } 
                value={char.name}
            />
        </View>
    )


}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#003f5c',
      alignItems: 'center',
      justifyContent: 'center',
    },
    logo: {
      fontWeight: 'bold',
      fontSize: 25,
      color: '#fb5b5a',
      marginBottom: 40,
    },
    inputView: {
      width: '80%',
      backgroundColor: '#465881',
      borderRadius: 25,
      height: 50,
      marginBottom: 20,
      justifyContent: 'center',
      padding: 20,
    },
    inputText: {
      height: 50,
      color: 'white',
    },
    loginBtn: {
      width: '80%',
      backgroundColor: '#fb5b5a',
      borderRadius: 25,
      height: 50,
      alignItems: 'center',
      justifyContent: 'center',
      marginTop: 40,
      marginBottom: 10,
    },
    loginText: {
      color: 'white',
    },
  });

export default CharacterCreationComponent;
