import React from 'react';
import {
    Platform,
    Button,
    TextInput,
    Text,
    View,
    TouchableNativeFeedback,
    TouchableOpacity,
    TouchableHighlight,
    StyleSheet,
} from 'react-native';
import styles from '../global-styles';
import { Character } from './character';


interface CreateProp {
    navigation: any;
}

export function CharacterCreationComponent({ navigation }: CreateProp) {
    const char: Character = new Character();

    function submitForm() {

    }
    function RadioButton(props: any) {
        return (
            <View style={[{
                height: 25,
                width: 25,
                borderRadius: 12,
                borderWidth: 2,
                borderColor: '#000',
                alignItems: 'center',
                justifyContent: 'center',
                alignSelf: 'center'
            }, styles.radioText]}>
                {
                    props.selected ?
                        <View style={{
                            height: 12,
                            width: 12,
                            borderRadius: 6,
                            backgroundColor: 'black',
                        }} />
                        : null
                }
            </View>
        );
    }
    return (
        <View style={styles.charContainer}>
            <View style={styles.charInputBox}>
                <Text style={styles.charLabel}>Character Name: </Text>
                <TextInput
                    style={styles.charInputText}
                    placeholder='Charater Name ...'
                    placeholderTextColor='white'
                /* value={char.name} */
                />
            </View>
            <View style={styles.radioContainer}>
                <Text style={styles.radioLabel}>Race</Text>
                <View style={styles.radioBox}>

                    <TouchableOpacity onPress={() => { }} style={styles.radio} >
                        {RadioButton('test')}
                        <Text style={styles.radioText}>Testing</Text>
                    </TouchableOpacity >

                    <TouchableOpacity onPress={() => { }} style={styles.radio} >
                        {RadioButton('test')}
                        <Text style={styles.radioText}>Testing2</Text>
                    </TouchableOpacity >

                </View>
            </View>
        </View>
    )


}


export default CharacterCreationComponent;
