import React from 'react';
import {
    Platform,
    Button,
    TextInput,
    Text,
    View,
    TouchableNativeFeedback,
    TouchableOpacity,
    TouchableHighlight,
    StyleSheet,
} from 'react-native';
import styles from '../global-styles';
import { Character } from './character';


interface CreateProp {
    navigation: any;
}

export function CharacterCreationComponent({ navigation }: CreateProp) {
    const char: Character = new Character();

    function submitForm() {

    }
    function RadioButton(props: any) {
        return (
            <View style={[{
              height: 24,
              width: 24,
              borderRadius: 12,
              borderWidth: 2,
              borderColor: '#000',
              alignItems: 'center',
              justifyContent: 'center',
              alignSelf: 'center'
            }, styles.charLabel]}>
              {
                props.selected ?
                  <View style={{
                    height: 12,
                    width: 12,
                    borderRadius: 6,
                    backgroundColor: '#000',
                  }}/>
                  : null
              }
            </View>
        );
      }
    return (
        <View style={styles.charContainer}>
            <View style={styles.charInputBox}>
                <Text style={styles.charLabel}>Character Name: </Text>
                <TextInput
                    style={styles.charInputText}
                    placeholder='Charater Name ...'
                    placeholderTextColor='white'
                /* value={char.name} */
                />
            </View>
            <View style={styles.charInputBox}>
                <TouchableOpacity onPress={()=>{}}>
                {RadioButton('test')}
                </TouchableOpacity >
                
                


            </View>
        </View>
    )


}


export default CharacterCreationComponent;
